mapper.update:
update `event`
name = #{name},
date = #{date}, time = #{time}, location = #{location}, description = #{description},
participantid = #{participantid}, sportid = #{sportid}
where id = #{id}

mapper.select:
SELECT * from event


        and sportid =  #{sportid}


        and date >= #{date_from}


    and date <= #{date_to}


    and time >= #{time_from}


    and time <= #{time_to}

    and NOT FIND_IN_SET(#{userid}, participantid)
